def create_grustu (id1, id2, id3, name)
  entry1 = Entry.find(id1);
  entry2 = Entry.find(id2);
  entry3 = Entry.find(id3);

  p = Program.new()
  p.title = name
  p.save
  p.program_entries.create(:entry => entry1, :order => 100)
  p.program_entries.create(:entry => entry2, :order => 200)
  p.program_entries.create(:entry => entry3, :order => 300)
end

